class UserAlreadyExistsException(Exception):
    """Exception raised when the user already exists in Cognito."""

    pass


class CognitoServiceException(Exception):
    """Exception raised for general Cognito service errors."""

    pass


class UserNotConfirmedException(Exception):
    """Exception raised when the user is not confirmed in Cognito."""

    pass


class LimitExceededException(Exception):
    """Exception raised when the limit exceeds in Cognito."""

    pass


class DatabaseOperationException(Exception):
    """Exception raised when database operations fail."""

    pass


class ValidationException(Exception):
    """Exception raised when validation fails."""

    pass


class UserNotFoundException(Exception):
    """Exception raised when the user is not found in the local database."""

    pass


class InvalidCredentialsException(Exception):
    """Exception raised for invalid email or password."""

    pass
