from ragent.connectors.email_sender import EmailSenderFactory
from ragent.connectors.email_sender.providers import EmailProvider


def send_notification_email(
    from_email: str, to_email: str, email_body: str, subject: str
):
    sender = EmailSenderFactory.get_email_sender(EmailProvider.AWS)
    return sender.send_email(from_email, to_email, email_body, subject)
