BLOG_EXTRACTION_COMBINED_PROMPT = (
    lambda content, fields_info, model: f"""
        You are an AI assistant that extracts structured information from content.
        Extract the following fields from the provided content based on the Pydantic model {model.__name__}:

        {fields_info}

        Return ONLY a JSON object with the specified fields.
        NOTE:
        1. If you can't find the required fields, return the fields with empty values.
        2. Make sure to provide the overall content.

        Extract the structured information from this content:

        {content}
        """
)


PROMPT_TEMPLATE = (
    lambda escaped_system_prompt: f"""{escaped_system_prompt}

    Context information is below:
    ---------------
    {{content}}
    ---------------

    Previous conversation:
    {{chat_history}}

    Current question: {{question}}
    Assistant: Let me help you with that question."""
)


GENERATE_PROMPT = (
    lambda base_prompt, content, user_input: f"""{base_prompt}
Here is the content:
{content}

User Question:
{user_input}
"""
)
