from ragent.connectors.llm.tts.base import TextToSpeech
from ragent.connectors.llm.tts.providers import TTSProviders
from ragent.connectors.llm.tts.providers.openai import OpenAITextToSpeech

"""
How to use:
llm = TTSFactory.get_tts_provider(TTSProviders.OPENAI)
print(llm.process_text(input="Hello", voice="alloy"))
"""


class TTSFactory:
    @staticmethod
    def get_tts_provider(provider: TTSProviders) -> TextToSpeech:
        if provider == TTSProviders.OPENAI:
            return OpenAITextToSpeech()
        else:
            raise ValueError(
                f"Unsupported provider: {provider}. Available: {TTSProviders.values()}"
            )
