from ragent.aws.secrets_manager.envs import get_secret_value_from_secret_manager

BUCKET = get_secret_value_from_secret_manager("BUCKET_NAME")
CLOUDFRONT_URL = get_secret_value_from_secret_manager("CLOUDFRONT_URL")

db_port = get_secret_value_from_secret_manager("DATABASE_PORT")
if db_port is None:
    db_port = "5432"  # Default port for PostgreSQL

DATABASE_URI = (
    f"postgresql://"
    f'{get_secret_value_from_secret_manager("DATABASE_USER")}:'
    f'{get_secret_value_from_secret_manager("DATABASE_PASS")}@'
    f'{get_secret_value_from_secret_manager("DATABASE_HOST")}:'
    f"{db_port}/"
    f'{get_secret_value_from_secret_manager("DATABASE_NAME")}'
)

REDIS_HOST = get_secret_value_from_secret_manager("REDIS_HOST")
REDIS_PORT = get_secret_value_from_secret_manager("REDIS_PORT")
REDIS_URI = f"redis://{REDIS_HOST}:{REDIS_PORT}"

TAVILY_API_KEY = get_secret_value_from_secret_manager("TAVILY_API_KEY")
COHERE_API_KEY = get_secret_value_from_secret_manager("COHERE_API_KEY")
COHERE_MODEL = get_secret_value_from_secret_manager("COHERE_MODEL")
HUGGINGFACE_API_KEY = get_secret_value_from_secret_manager("HUGGINGFACE_API_KEY")
