import logging
import sys

logger = logging.getLogger(__name__)
logging.basicConfig(
    stream=sys.stdout,  # Output to stdout instead of stderr
    level=logging.INFO,  # Set to INFO level
    format="%(asctime)s - %(name)s - %(levelname)s - %(message)s",
    datefmt="%Y-%m-%d %H:%M:%S",
    force=True,  # Force configuration even if already configured
)
