"""
Custom exceptions for Whisper transcription package
"""


class WhisperTranscriptionError(Exception):
    """Base exception for all Whisper transcription errors"""

    pass


class AudioValidationError(WhisperTranscriptionError):
    """Raised when audio file validation fails"""

    pass


class HallucinationDetectedError(WhisperTranscriptionError):
    """Raised when Whisper hallucination is detected in transcript"""

    pass


class WhisperAPIError(WhisperTranscriptionError):
    """Raised when OpenAI Whisper API returns an error"""

    pass
