"""Transcriber provider enumeration."""

from enum import Enum


class TranscriberProviders(Enum):
    """Enumeration of available transcription providers."""

    OPENAI = "openai"
    # Future providers can be added here:
    # GEMINI = "gemini"
    # AZURE = "azure"

    @classmethod
    def values(cls):
        """Return list of all provider values."""
        return [e.value for e in cls]
