"""
Custom exceptions for Voice Form Filler package.
"""


class VoiceFormError(Exception):
    """Base exception for voice form filler errors."""

    pass


class AudioProcessingError(VoiceFormError):
    """Raised when audio processing fails."""

    pass


class TextParsingError(VoiceFormError):
    """Raised when text parsing fails."""

    pass


class ValidationError(VoiceFormError):
    """Raised when configuration or input validation fails."""

    pass
