"""Custom exceptions for the voice package"""


class VoiceGatewayException(Exception):
    """Base exception for all voice gateway errors"""

    def __init__(self, message: str, error_code: str = None):
        super().__init__(message)
        self.message = message
        self.error_code = error_code


class AudioValidationError(VoiceGatewayException):
    """Raised when audio validation fails"""

    def __init__(self, message: str, audio_info: dict = None):
        super().__init__(message, "AUDIO_VALIDATION_ERROR")
        self.audio_info = audio_info or {}


class SessionError(VoiceGatewayException):
    """Raised when session operations fail"""

    def __init__(self, message: str, client_id: str = None):
        super().__init__(message, "SESSION_ERROR")
        self.client_id = client_id


class WebSocketConnectionError(VoiceGatewayException):
    """Raised when WebSocket connection fails"""

    def __init__(self, message: str, client_id: str = None):
        super().__init__(message, "WEBSOCKET_CONNECTION_ERROR")
        self.client_id = client_id


class ConfigurationError(VoiceGatewayException):
    """Raised when configuration is invalid"""

    def __init__(self, message: str, config_section: str = None):
        super().__init__(message, "CONFIGURATION_ERROR")
        self.config_section = config_section


class AudioProcessingError(VoiceGatewayException):
    """Raised when audio processing fails"""

    def __init__(
        self, message: str, client_id: str = None, audio_duration: float = None
    ):
        super().__init__(message, "AUDIO_PROCESSING_ERROR")
        self.client_id = client_id
        self.audio_duration = audio_duration
