import React, { useState } from 'react';
import { Mic, MicOff, Volume2, Loader2, X } from 'lucide-react';
import { useVoice } from '../context/VoiceContext';

/**
 * Voice Controller Component
 *
 * Floating button that:
 * - Starts/stops voice session
 * - Shows current state (listening, processing, speaking)
 * - Displays transcripts
 * - Persists across all pages
 */
function VoiceController() {
  const {
    isConnected,
    isListening,
    isProcessing,
    isSpeaking,
    userTranscript,
    assistantTranscript,
    startVoice,
    stopVoice,
  } = useVoice();

  const [isExpanded, setIsExpanded] = useState(false);

  const handleToggle = async () => {
    if (isConnected) {
      stopVoice();
      setIsExpanded(false);
    } else {
      await startVoice();
      setIsExpanded(true);
    }
  };

  // Determine current state
  const getState = () => {
    if (!isConnected) return 'idle';
    if (isSpeaking) return 'speaking';
    if (isProcessing) return 'processing';
    if (isListening) return 'listening';
    return 'connected';
  };

  const state = getState();

  // State-based styling
  const stateStyles = {
    idle: 'bg-primary-600 hover:bg-primary-700',
    listening: 'bg-green-500 voice-pulse',
    processing: 'bg-yellow-500',
    speaking: 'bg-blue-500',
    connected: 'bg-primary-600',
  };

  const stateIcons = {
    idle: <Mic className="w-6 h-6" />,
    listening: <Mic className="w-6 h-6" />,
    processing: <Loader2 className="w-6 h-6 animate-spin" />,
    speaking: <Volume2 className="w-6 h-6" />,
    connected: <Mic className="w-6 h-6" />,
  };

  const stateLabels = {
    idle: 'Start Voice',
    listening: 'Listening...',
    processing: 'Processing...',
    speaking: 'Speaking...',
    connected: 'Voice Active',
  };

  return (
    <div className="fixed bottom-6 right-6 z-50 flex flex-col items-end space-y-3">
      {/* Transcript Panel */}
      {isExpanded && isConnected && (
        <div className="bg-white rounded-2xl shadow-2xl border border-gray-200 w-80 overflow-hidden animate-in slide-in-from-bottom-4">
          {/* Header */}
          <div className="bg-gradient-to-r from-primary-600 to-primary-700 px-4 py-3 flex justify-between items-center">
            <div className="flex items-center space-x-2">
              <div className="flex space-x-1">
                {/* Waveform animation */}
                {(isListening || isSpeaking) && (
                  <div className="flex items-center space-x-0.5 h-4">
                    {[1, 2, 3, 4, 5].map((i) => (
                      <div
                        key={i}
                        className="w-1 bg-white rounded-full waveform-bar"
                        style={{
                          height: `${Math.random() * 12 + 4}px`,
                          animationDelay: `${i * 0.1}s`
                        }}
                      />
                    ))}
                  </div>
                )}
              </div>
              <span className="text-white text-sm font-medium">
                {stateLabels[state]}
              </span>
            </div>
            <button
              onClick={() => setIsExpanded(false)}
              className="text-white/80 hover:text-white transition-colors"
            >
              <X className="w-4 h-4" />
            </button>
          </div>

          {/* Transcripts */}
          <div className="p-4 max-h-64 overflow-y-auto space-y-3">
            {/* User transcript */}
            {userTranscript && (
              <div className="flex justify-end">
                <div className="bg-primary-100 text-primary-800 px-3 py-2 rounded-2xl rounded-br-md max-w-[80%] text-sm">
                  {userTranscript}
                </div>
              </div>
            )}

            {/* Processing indicator */}
            {isProcessing && (
              <div className="flex justify-start">
                <div className="bg-gray-100 px-3 py-2 rounded-2xl rounded-bl-md">
                  <div className="flex space-x-1">
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
                    <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
                  </div>
                </div>
              </div>
            )}

            {/* Assistant transcript */}
            {assistantTranscript && !isProcessing && (
              <div className="flex justify-start">
                <div className="bg-gray-100 text-gray-800 px-3 py-2 rounded-2xl rounded-bl-md max-w-[80%] text-sm">
                  {assistantTranscript}
                </div>
              </div>
            )}

            {/* Empty state */}
            {!userTranscript && !assistantTranscript && !isProcessing && (
              <div className="text-center text-gray-400 py-4">
                <Mic className="w-8 h-8 mx-auto mb-2 opacity-50" />
                <p className="text-sm">Say something like:</p>
                <p className="text-xs mt-1 italic">"Show me mobile phones under 10,000"</p>
              </div>
            )}
          </div>

          {/* Footer with tips */}
          <div className="px-4 py-2 bg-gray-50 border-t border-gray-100">
            <p className="text-xs text-gray-500 text-center">
              {isSpeaking ? 'ðŸ”Š Playing response...' : isListening ? 'ðŸŽ¤ Speak now...' : 'Voice assistant ready'}
            </p>
          </div>
        </div>
      )}

      {/* Main Voice Button */}
      <button
        onClick={handleToggle}
        className={`
          w-14 h-14 rounded-full shadow-lg flex items-center justify-center
          text-white transition-all duration-300 transform hover:scale-105
          focus:outline-none focus:ring-4 focus:ring-primary-300
          ${stateStyles[state]}
        `}
        title={isConnected ? 'Stop Voice' : 'Start Voice Assistant'}
      >
        {isConnected ? (
          state === 'idle' ? <MicOff className="w-6 h-6" /> : stateIcons[state]
        ) : (
          <Mic className="w-6 h-6" />
        )}
      </button>

      {/* Quick tip when idle */}
      {!isConnected && (
        <div className="bg-white px-3 py-1.5 rounded-full shadow-md text-xs text-gray-600 animate-pulse">
          Click to start voice shopping
        </div>
      )}
    </div>
  );
}

export default VoiceController;
